import { useState, useEffect } from 'react';
import "./main.css";
import Axios from "axios";

const ThreadTitles = () => {
  const [titles, setTitles] = useState([]);
  useEffect(() => {
    Axios.get("http://localhost:3000/api/get/titles").then((response) => {
      setTitles(response.data);
    });
  }, []);

  return {
    titles
  }
}
