import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import TopTitle from "./../Header";
import Footer from "./../Footer";
import AThreadsArea from "./../AThreadsArea";

const AllThreadPage = () => {
  const { id } = useParams();
  const [comments, setComments] = useState([]);
  const [title, setTitle] = useState([]);

  // 投稿データ取得
  useEffect(() => {
    const url = `http://localhost:3000/api/get/comments/${id}`;
    axios
      .get(url)
      .then((res) => setComments(res.data))
      .catch((error) => console.log(error));
  }, [setComments, id]);
  useEffect(() => {
    const url = `http://localhost:3000/api/get/title/${id}`;
    axios
      .get(url)
      .then((res) => setTitle(res.data.title))
      .catch((error) => console.log(error));
  }, [setTitle, id]);
  console.log(comments)
  return (
    <>
      <TopTitle />
      {comments.map((value, index) => {
            return (
              <div key={index} className="threadsArea">
                <AThreadsArea
                  title={value.title}
                  comments={value.comments}
                  count="10"
                  comment_count={value.count}
                  title_id={value.id}
                />
              </div>
            );
          })}

      {id}
      <Footer />
    </>
  );
};

export default AllThreadPage;
