import { useParams } from "react-router-dom";
import TopTitle from "./../Header";
import Footer from "./../Footer";
import AThreadsArea from "./../AThreadsArea";

const AllThreadPage = (props) => {
  const params = useParams();
  const titleId = params.id;
  const dataList = useContext(DataList);
  console.log(dataList)
  // const newList = dataList.map((val) => {
  //   return val.titleData.title_id === titleId;
  // });

  return (
    <>
      <TopTitle />
      {titleId}
      {dataList.map((value, index) => {
            return (
              <div key={index} className="threadsArea">
                <AThreadsArea
                  title={value.titleData.title}
                  comments={value.comments}
                  count={props.count}
                  comment_count={value.titleData.count}
                  title_id={value.titleData.id}
                />
              </div>
            );
          })}
      <Footer />
    </>
  );
};

export default AllThreadPage;
