import { useContext } from "react";
import { useState } from "react";
import "./main.css";
import { Comments } from "./App";

const NewComments = (props) => {
  const comments = useContext(Comments);
  const title = props.title
  // const submitComment = (e) => {
  //   setNewComments((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  // };
  // const submitComment = () => {


  return (
    <div className="formContainer">
      <form action="">
        <label htmlFor="名前">名前：</label>
        <input id="name" type="text" name="name" />
        <label htmlFor="email">email：</label>
        <input id="email" type="text" name="email" />
        <label htmlFor="タイトル">タイトル</label>
        <input id="title" type="text" name="title" />
        <br />
        <textarea id="message" name="message" rows="10"></textarea>
        <button onClick="submitComment">書き込む</button>
      </form>


      <div className="newTitle">
      <div className="formContainer">
        <form onSubmit={(e) => handleSubmit(e)}>
          <h2>新規スレッド</h2>
          <div className="uniForm">
            <div className="formField">
              <label htmlFor="タイトル">タイトル</label>
              <br />
              <input
                id="title"
                type="text"
                name="title"
                onChange={(e) => handleChange(e)}
              />
              <p className="errorMsg">{formErrors.title}</p>
            </div>
            <div className="formField">
              <label htmlFor="名前">名前</label>
              <br />
              <input
                id="name"
                type="text"
                name="name"
                onChange={(e) => handleChange(e)}
              />
              <p className="errorMsg">{formErrors.name}</p>
            </div>
            <div className="formField">
              <label htmlFor="message">コメント</label>
              <br />
              <textarea
                rows="10"
                id="message"
                type="text"
                name="message"
                onChange={(e) => handleChange(e)}
              />
              <p className="errorMsg">{formErrors.message}</p>
            </div>
          </div>
          <button className="submitButton">投稿</button>
        </form>
      </div>
    </div>

    </div>
  );
};

export default NewComments;
