import React, { userState, useEffect } from 'react';
import ReactDOM from 'react-dom/client';
// import App from './App';
import "./App.css";
import Axios from "axios";

function App() {
  const [threads, setThreads] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3000/api/get/threads").then((respons) => {
      setThreads(response.data);
    });
  }, []);
  return (
    <div className="App">
      <ul>
        {threads.map((val, index) => {
          return <li key={index}>{val.username}</li>
        })}
      </ul>
    </div>
  )
}

export default App;

