import { useState, useEffect } from "react";
import "./main.css";
import Axios from "axios";
// import useTitles from "./useTitles";

function ThreadComments(board_id) {
  const [comments, setComments] = useState([]);
  useEffect(() => {
    Axios.get("http://localhost:3000/api/get/comments")
      .then((response) => {
        setComments(response.data);
      })
      .then(
        Object.values(comments).filter((val) => {
          return (val.board_id = 1);
        })
      );
  }, []);

  // Object.values(comments).sort((a,b) => {
  //   return a.post_date - b.post_date
  // })
  return (
    <>
      <h2>
        No{comments[0].id} {comments[0].title}
      </h2>
      <h3>1 名前：{comments[0]}</h3>
      {comments.forEach((val) => {})}
    </>
  );
}
export default ThreadComments;
