import { useState, useEffect } from 'react';
import "./main.css";
import Axios from "axios";

const ThreadTitles = () => {
  const [titles, setTitles] = useState([]);
  useEffect(() => {
    Axios.get("http://localhost:3000/api/get/titles").then((response) => {
      setTitles(response.data);
    });
  }, []);

  return {
    titles
  }
}

function App() {
  const [list, setList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3000/api/get/threads").then((response) => {
      setList(response.data);
    });
    // 特定のステート(countなど)を入れると、それ(count)の更新時のみ実行
  }, []);

  return (
    <div className="App">
      <ul>
        {list.map((val, index) => {
          return <li key={index}>{val.title}</li>
        })}
      </ul>
    </div>
  )
}

export default App;
