import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import TopTitle from "./../Header";
import Footer from "./../Footer";
import AThreadsArea from "./../AThreadsArea";

const AllThreadPage = () => {
  const { id } = useParams();
  const [comments, setComments] = useState([]);

  // 投稿データ取得
  useEffect(() => {
    const url = `http://localhost:3000/api/get/comments/${id}`;
    axios
      .get(url)
      .then((res) => setComments(res.data))
      .catch((error) => console.log(error));
  }, [setComments]);
  return (
    <>
      <TopTitle />
      {/* {comments.map((value, index) => {
            return (
              <div key={index} className="threadsArea">
                <AThreadsArea
                  title={value.title}
                  comments={value.comments}
                  count="10"
                  comment_count={value.count}
                  title_id={value.id}
                />
              </div>
            );
          })} */}

      {id}
      <Footer />
    </>
  );
};

export default AllThreadPage;
