import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom/client';
import Axios from "axios";
import "./main.css";
import App from './App';
import TopTitle from './TopTitle';
import AllThreads from './AllThreads';

const ThreadsList = 
// データベースのデータ取得
ThreadsList = () => {
  const [threads, setThreads] = useState([]);

  // 副作用のある関数を遅延読み込み useEffect(副作用関数, [実行のタイミング])
  useEffect(() => {
    Axios.get("http://localhost:3000/api/get/threads").then((response) => {
      setThreads(response.data);
    });
    // 空の配列：初回のみ実行。第二引数の配列を省略するとレンだリングの度に毎回実行され、無限ループになる
    // 特定のステート(countなど)を入れると、それ(count)の更新時のみ実行
  }, []);
  return threads
}
console.log(ThreadsList)

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <TopTitle />
    <button href="/">新規スレッドを書く</button>
    <AllThreads />
    <App />
    {ThreadsList[0]}
  </React.StrictMode>
);
