import ThreadTitle from "./ThreadTitle";
import ThreadComments from "./ThreadComments";
import ShowNewComments from "../links&btns/ShowNewComments";
import { useState } from "react";
import ReadAllLink from "../links&btns/ReadAllLink";
import LikesTitleBtn from "../links&btns/LikesTitleBtn";
import NewComments from "../forms/NewComments";


const AThreadsArea = (props) => {
  const [isClick, setIsClick] = useState(false);
  const handleClick = () => {
    setIsClick(!isClick);
  };

  return (
    <>
      <ThreadTitle title_id={props.title_id} title={props.title} />
      <ThreadComments
        comments={props.comments}
        count={props.count}
        comment_count={props.comment_count}
      />
      <div className="threadBtnLinks">
        <ShowNewComments onClick={() => handleClick()} title_id={props.title_id} title={props.title} />
      {isClick && <NewComments title_id={props.title_id} title={props.title} />}

        <LikesTitleBtn title_id={props.title_id} />
        <ReadAllLink
          title_id={props.title_id}
          comment_count={props.comment_count}
        />
      </div>
    </>
  );
};

export default AThreadsArea;
