import { useContext } from "react";
import { Titles } from "./App";
import { Comments } from "./App";
import ThreadTitle from "./ThreadTitle";
import ThreadComments from "./ThreadComments";
import ShowNewComments from "./ShowNewComments";

const ThreadsAreas = (props) => {
  const titles = useContext(Titles);
  const comments = useContext(Comments);
  const commentsList = []
  for (let i = 0; i < titles.length; i++) {
    for (let j = 0; j < titles.length; j++) {
      if (titles[i].id === comments[j].title_id) {
        commentsList[j].push(comments[j])
      }
    }
  }
  titles.map((title) => {
    return (
      comments.forEach((comment) => {
        if (title.id === comment.title_id) {
          commentsList.push(comment)
        }
      })
    )
  })

  return (
    <div className="thread">
      <div className="threadWrap">
        <div className="threadsAreas">
          {titles.map((title, idxT) => {
            return (
              <div key={idxT}>
                <ThreadTitle title={title.title} />
                {
                  comments.map((comment, index) => {
                    return (
                      title.id === comment.title_id && (
                        <ThreadComments
                          comments={commentsList}
                          count={props.count}
                        />
                      )
                    )
                  })
                }
                <ShowNewComments title_id={title.id} title={title.title} />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default ThreadsAreas;
