import React, { useState, useContext, createContext } from "react";
import Header from "./Header";
import WriteNewTitle from "./WriteNewTitle";
import ThreadTitles from "./ThreadTitles";
import ThreadsAreas from "./ThreadsAreas";
import SideMenus from "./SideMenus";
import Footer from "./Footer";
import { Titles, Comments } from "./App";

export const DataList = createContext();

const MainPage = () => {
  const titles = useContext(Titles);
  const comments = useContext(Comments);

  const dataList = [];
  
  // dataListに{タイトルデータ：コメント一覧}を格納
  for (let i = 0; i < titles.length; i++) {
    commentsList[i] = { titleData: titles[i], comments: [] };
    for (let j = 0; j < comments.length; j++) {
      if (titles[i].id === comments[j].title_id) {
        commentsList[i].comments.push(comments[j]);
      }
    }
  }
  console.log(dataList)

  return (
    <>
      <Header />
      <main>
        <DataList.Provider value={titles}>
          <SideMenus />
          <WriteNewTitle />
          <ThreadTitles />
          <ThreadsAreas count="4" />
        </DataList.Provider>
      </main>
      <Footer />
    </>
  );
};

export default MainPage;
