import React, { useState, useEffect, createContext } from "react";
import axios from "axios";
import Header from "./Header";
import WriteNewTitle from "./WriteNewTitle";
import ThreadTitles from "./ThreadTitles";
import ThreadsAreas from "./ThreadsAreas";
import SideMenus from "./SideMenus";
import Footer from "./Footer";

export const Comments = createContext();
export const Titles = createContext();
export const CommentsList = createContext();

const App = () => {
  const [comments, setComments] = useState([]);
  const [titles, setTitles] = useState([]);
  // 投稿データ取得
  useEffect(() => {
    const url = "http://localhost:3000/api/get/comments";
    axios
      .get(url)
      .then((res) => setComments(res.data))
      .catch((error) => console.log(error));
  }, [setComments]);
  // タイトル一覧データ取得
  useEffect(() => {
    const url = "http://localhost:3000/api/get/titles";
    axios
      .get(url)
      .then((res) => setTitles(res.data))
      .catch((error) => console.log(error));
  }, [setTitles]);

  const commentsList = [];

  // commentsListに{タイトルデータ：コメント一覧}を格納
  for (let i = 0; i < titles.length; i++) {
    commentsList[i] = { titleData: titles[i], comments: [] };
    for (let j = 0; j < comments.length; j++) {
      if (titles[i].id === comments[j].title_id) {
        commentsList[i].comments.push(comments[j]);
      }
    }
  }
  console.log(commentsList)
  return (
    <>
      <Header />
      <main>
        <Comments.Provider value={comments}>
          <Titles.Provider value={titles}>
            <SideMenus />
            <WriteNewTitle />
            <ThreadTitles />
            <ThreadsAreas count="4" />
          </Titles.Provider>
        </Comments.Provider>
      </main>
      <Footer />
    </>
  );
};

export default App;
