import React, { useState, useEffect } from 'react';
import "./main.css";
import Axios from "axios";

function App() {
  const [list, setList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3000/api/get/threads").then((response) => {
      setList(response.data);
    });
    // 特定のステート(countなど)を入れると、それ(count)の更新時のみ実行
  }, []);

  // const submitThreads = () => {
  //   Axios.post("http://localhost:3000/api/insert/threads", {
  //     name: threadsName,
  //   }).then(() => {
  //     alert("successful insert")
  //   })
  // }

  return (
    <div className="App">
      <ul>
        {list.map((val, index) => {
          return <li key={index}>{val.title}</li>
        })}
      </ul>
    </div>
  )
}

export default App;
