import { Link } from "react-router-dom";
import React, { useEffect, useState, useContext } from "react";
import axios from "axios";


const ReadAllLink = (props) => {
  const [isClick, setIsClick] = useState();
  const handleClick = (e) => {
    console.log(e.target)
  };
const [title, setTitle] = useState([]);
  const [comments, setComments] = useState([]);

  // 投稿データ取得
  useEffect(() => {
    const url = `http://localhost:3000/api/get/titles/${id}`;
    axios
      .get(url)
      .then((res) => setTitle(res.data))
      .catch((error) => console.log(error))
  }, [setTitle, id]);
  useEffect(() => {
    const url = `http://localhost:3000/api/get/comments/${id}`;
    axios
      .get(url)
      .then((res) => setComments(res.data))
      .catch((error) => console.log(error));
  }, [setComments, id]);

  const link = `/allThread/${props.title_id}`
  return (
    <>
      {/* <Link to={link}>全部読む({props.comment_count})</Link> */}
      <button onClick={(e) => handleClick()}>全部読む({props.comment_count})
      </button>
    </>
  );
};

export default ReadAllLink;
