import React, { useState, useEffect, createContext } from "react";
import ReactDOM from "react-dom/client";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import App from "./App";
import LogInPage from "./pages/LogInPage";
import SignUpForm from "./pages/SignUpPage";
import NewTitlePage from "./pages/NewTitlePage";
import SuccessSignUp from "./pages/SuccessSignUp";
import SuccessPostTitle from "./pages/SuccessPostTitle";
import ThreadPage from "./pages/ThreadPage";
import UserLikesPage from "./pages/UserLikesPage";
import AllThreadPage from "./pages/AllThreadPage";
import "./main.css";

export const Comments = createContext();
export const Titles = createContext();

const root = ReactDOM.createRoot(document.getElementById("root"));
const [comments, setComments] = useState([]);
const [titles, setTitles] = useState([]);
// 投稿データ取得
useEffect(() => {
  const url = "http://localhost:3000/api/get/comments";
  axios
    .get(url)
    .then((res) => setComments(res.data))
    .catch((error) => console.log(error));
}, [setComments]);
// タイトル一覧データ取得
useEffect(() => {
  const url = "http://localhost:3000/api/get/titles";
  axios
    .get(url)
    .then((res) => setTitles(res.data))
    .catch((error) => console.log(error));
}, [setTitles]);
root.render(
  <React.StrictMode>
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<App />}></Route>
        <Route path="newTitle" element={<NewTitlePage />}></Route>
        <Route path="login" element={<LogInPage />}></Route>
        <Route path="signup" element={<SignUpForm />}></Route>
        <Route path="successSignUp" element={<SuccessSignUp />}></Route>
        <Route path="successPostTitle" element={<SuccessPostTitle />}></Route>
        <Route path="success" element={<SuccessSignUp />}></Route>
        <Route path="threadPage" element={<ThreadPage />}></Route>
        <Route path="userLikes" element={<UserLikesPage />}></Route>
        <Route path="AllThread" element={<AllThreadPage />}></Route>
      </Routes>
    </BrowserRouter>
  </React.StrictMode>
);
