import React, { useContext } from "react";
import { useParams } from "react-router-dom";
import TopTitle from "../Header";
import Footer from "./../Footer";
import AThreadsArea from "./../AThreadsArea";
import { DataList } from "../MainPage";

const AllThreadPage = (props) => {
  const params = useParams();
  const titleId = params.id;
  const dataList = useContext(DataList);
  const newList = dataList.map((val) => {
    return val.titleData.title_id === titleId;
  });

  return (
    <>
      <TopTitle />
      {titleId}
      <AThreadsArea
      title={newList.titleData.title}
      comments={newList.comments}
      count={props.count}
      comment_count={newList.titleData.count}
      title_id={newList.titleData.id}
      />
      <Footer />
    </>
  );
};

export default AllThreadPage;
