import React, { useState, useEffect } from 'react';
import "./main.css";
import Axios from "axios";

function ThreadTitles() {
  const [comments, setComments,titles, setTitles] = useState([]);
  useEffect(() => {
    Axios.get("http://localhost:3000/api/get/comments").then((response) => {
      setComments(response.data);
    });
  }, []);
  useEffect(() => {
    Axios.get("http://localhost:3000/api/get/titles").then((response) => {
      setTitles(response.data);
    });
  }, []);

  return {
    comments,
    titles,
  }
}
export default ThreadTitles;
