import { createSlice } from "@redux/toolkit";

export const counterSlice = createSlice({
  name: "isLoggedIn",
  initialState: {
    value: true,
  },
  reducers: {
    login: (state) => {
      state.value = true;
    },
    logout: (state) => {
      state.value = false
    }
  }
})

export const { login, logout } = counterSlice.actions;
export default counterSlice.reducer;
