import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import TopTitle from "../dom/Header";
import Footer from "../dom/Footer";
import AThreadsArea from "../threads/AThreadsArea";

const AllThreadPage = () => {
  const { id } = useParams();
  const [comments, setComments] = useState([]);
  const [title, setTitle] = useState([]);

  // 投稿データ取得
  useEffect(() => {
    const url = `http://localhost:3000/api/get/comments/${id}`;
    axios
      .get(url)
      .then((res) => setComments(res.data))
      .catch((error) => console.log(error));
  }, [id]);
  useEffect(() => {
    const url = `http://localhost:3000/api/get/title/${id}`;
    axios
      .get(url)
      .then((res) => setTitle(res.data))
      .catch((error) => console.log(error));
  }, [id]);
  return (
    <>{id}
      <TopTitle />
        <div className="threadsArea">
          <AThreadsArea
            title={title.title}
            comments={comments}
            count={title.count}
            comment_count={title.count}
            title_id={id}
          />
        </div>
      <Footer />
    </>
  );
};

export default AllThreadPage;
